import React, { useState } from 'react';

const BudgetInput = ({onSubmit}) => {

	const [amountInput, setAmountInput] = useState(null);
	const [descriptionInput, setDescriptionInput] = useState('');
	const [typeInput, setTypeInput] = useState(null);


	const onBudgetSubmit = (event) => {
		event.preventDefault();
		if (!amountInput || !descriptionInput || typeInput === null) {
			alert('Please fill out all fields')
		} else {
			onSubmit(amountInput, descriptionInput, typeInput);
			document.querySelector('#description').value = '';
			document.querySelector('#amount').value = '';
		}
	}

	return (
		<form className="ui form">
		  <div className="fields">
		    <div className="field">
		      <label htmlFor="description">Description</label>
		      <input 
		      	onChange={(e) => setDescriptionInput(e.target.value)}
		      	id="description"
		      	type="text" 
		      	placeholder="Description"/>
		    </div>
		    <div className="field">
		      <label htmlFor="amount">Amount</label>
		      <input 
		      	onChange={(e) => setAmountInput(parseInt(e.target.value))}
		      	id="amount"
		      	type="text" 
		      	placeholder="$$"/>
		    </div>
		    <div className="field">
		     <label>Type</label>
					<select onChange={(e) => setTypeInput(parseInt(e.target.value))} id="budget-type" className="ui search dropdown" defaultValue={'DEFAULT'} style={{padding: '0 0 0 5px'}}>
					  <option value="DEFAULT" disabled>Select Type</option>
					  <option value="1">Income</option>
					  <option value="0">Expense</option>
					</select>
		    </div>
		    <div id="button-field" className="field">
			    <button 
			    	onClick={onBudgetSubmit}
			    	className="ui button" 
			    	type="submit">
			    	Submit
			    </button>
			   </div>
		  </div>
		</form>
	);
}

export default BudgetInput;
