import React from 'react';

const Balance = ({data}) => {

	const calculateBalance = () => {
		const cleanedData = data.map((transaction, index) => {
			if(transaction.type === 0) {
				transaction.amount * -1;
				return transaction
			}
		})
		return cleanedData.reduce((acc, num) => {
			return acc + num.amount }, 0)
	}

	return (
		<div className="ui container" id="balance-container">
			<h2>Balance</h2>
			<h5 id="balance-text" style={{color: calculateBalance() > 0 ? '#2f6f2f' : '#ce2525'}}>
				{data[0].amount ? `$${calculateBalance().toFixed(2)}` : ''}</h5>
		</div>
	);
}



export default Balance;

