import React from 'react';

const BudgetCard = ({amount, description, type}) => {

	return (
	<div className="budget-item">
		<div className="ui small horizontal statistic">
		  <div className="value" id="budget-value" style={{color: type === 1 ? '#2f6f2f' : '#ce2525'}}>
		    {amount ? type === 1 ? `+$${amount.toFixed(2).replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')}` : `-$${amount.toFixed(2).replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')}`: ''}
		  </div>
		  <div className="label" id="budget-description" style={{color: type === 1 ? '#2f6f2f' : '#ce2525'}}>
		    {description}
		  </div>
		</div>
	</div>
	);
}


export default BudgetCard;
