import React from 'react';

const Balance = ({data}) => {

	const calculatedBalance = data.reduce((acc, num) => ({amount: acc.amount + num.amount}))

	return (
		<div>
			<h2>{data[0].amount ? 'Balance' : ''}</h2>
			<h5>{data[0].amount ? calculatedBalance : ''}</h5>
		</div>
	);
}



export default Balance;

