import React, { useState, useEffect } from 'react';
import BudgetInput from './BudgetInput';
import BudgetList from './BudgetList';
import Balance from './Balance';
import Reset from './Reset';
import Signin from './Signin';
import Register from './Register';
import NavBar from './NavBar';
import './App.css';

const App = () => {

	const [data, setData] = useState([{ amount: null, description: '', type: null}]);
	const [route, setRoute] = useState('signin');
	const [isSignedIn, setIsSignedIn] = useState(false);
	const [signInUserID, setSignedInUserID] = useState(null);

	//setting user id piece of state to the id of the signed in user
	const loadUser = id => {
		setSignedInUserID(id);
	}

	//displaying whatever data exists in database on initial load
	useEffect(()=> {
		const initialGet = async () => {
			let initialData = await fetch('http://localhost:3001/');
			initialData = await initialData.json();
			if (initialData.length === 0) {
				setData([{ amount: null, description: '', type: null, date: ''}]);
			} else {
				initialData = initialData.map(({amount, description, type, date}) => ({amount: parseFloat(amount), description, type, date}));
				setData(initialData);
			}
		};
		initialGet()
	},[])

	//fetching updated data after submitting new transaction
	const fetchTransactions = async (amount, description, type, date, id) => {
		let response = await fetch('http://localhost:3001/submit', {
			method: 'put',
			headers: { 'Content-type': 'application/json'},
			body: JSON.stringify({
				amount: amount,
				description: description,
				type: type,
				date: date,
				id: id
			})
		})
		response = await response.json();
		response = response.map(({amount, description, type, date}) => ({amount: parseFloat(amount), description, type, date}));
		setData(response);
	}

	//clearing database after hitting reset
	const onReset = async () => {
		await fetch('http://localhost:3001/reset', {
			method: 'put',
			headers: { 'Content-type': 'application/json'},
		})
		setData([{ amount: null, description: '', type: null, date: ''}]);
	}

	//deleting desired transaction from databse
	const onDelete = async (amount, description, type, date) => {
		let newData = await fetch('http://localhost:3001/delete', {
			method: 'put',
			headers: { 'Content-type': 'application/json'},
			body: JSON.stringify({
				amount: amount,
				description: description,
				type: type,
				date: date
			})
		})
		newData = await newData.json();
		if (newData.length === 0) {
			setData([{ amount: null, description: '', type: null, date: ''}]);
		} else {
			newData = newData.map(({amount, description, type, date}) => ({amount: parseFloat(amount), description, type, date}));
			setData(newData);
		}
	}

	const onRouteChange = (route) => {
		if (route === 'home') {
			setIsSignedIn(true);
		} else {
			setIsSignedIn(false);
		}
		setRoute(route);
	}


	return (
		<div>
			<NavBar onRouteChange={onRouteChange} isSignedIn={isSignedIn}/>
			{route === 'signin' 
			? <Signin loadUser={loadUser} onRouteChange={onRouteChange}/> : 
			route === 'register' 
			? <Register loadUser={loadUser} onRouteChange={onRouteChange}/> : (
				<div>
					<div id="budget-input" className="ui container">
						<BudgetInput onSubmit={fetchTransactions} />
					</div>
					<div id="reset-button" className="ui container">
						<Reset onReset={onReset} />
					</div>
					<BudgetList data={data} onDelete={onDelete}/> 
					<Balance data={data}/>
				</div>
				)
			}
		</div>

	);

	// return (
	// 	<div>
	// 		<div id="budget-input" className="ui container">
	// 			<BudgetInput onSubmit={fetchTransactions} />
	// 		</div>
	// 		<div id="reset-button" className="ui container">
	// 			<Reset onReset={onReset} />
	// 		</div>
	// 		<BudgetList data={data} onDelete={onDelete}/> 
	// 		<Balance data={data}/> 
	// 	</div>

	// );
}

export default App;
