import React, { useState } from 'react';
import BudgetInput from './BudgetInput';
import BudgetList from './BudgetList';
import Balance from './Balance';
import Reset from './Reset';
import './App.css';

const dataSet = [
	{
		amount: 5,
		description: 'chipotle',
		type: 0
	},
	{
		amount: 500,
		description: 'paycheck',
		type: 1
	},
	{
		amount: 25.50,
		description: 'beer',
		type: 0
	}
]

const App = () => {

	// const [amount, setAmount] = useState(0);
	// const [description, setDescription] = useState('');
	// const [type, setType] = useState(true);

	const [data, setData] = useState([{ amount: 5, description: 'work', type: 0}]);

	// useEffect(() => {
	// 	setData(dataSet)
	// }, [])

	const addBudgetItem = async () => {
		// setData(test);
		let response = await fetch('http://localhost:3001/submit', {
			method: 'put',
			headers: { 'Content-type': 'application/json'},
			body: JSON.stringify({
				amount: data[0].amount,
				description: data[0].description,
				type: data[0].type
			})
		})
		response = await response.json();
		console.log(response);
	}

	const onReset = () => {
		setData([{amount: null, description: '', type: null}]);
	}


	return (
		<div>
			<div id="budget-input" className="ui container">
				<BudgetInput onSubmit={addBudgetItem} />
			</div>
			<div id="reset-button" className="ui container">
				<Reset onReset={onReset} />
			</div>
			<BudgetList data={data}/>
			<Balance data={data}/>
		</div>

	);
}

export default App;
