import React, { useState } from 'react';

const BudgetInput = ({onSubmit}) => {

	const [amount, setAmount] = useState(0);
	const [description, setDescription] = useState('');
	const [type, setType] = useState(true);


	const onBudgetSubmit = (event) => {
		event.preventDefault();
		onSubmit();
	}

	return (
		<div className="ui container">
			<form className="ui form">
			  <div className="fields">
			    <div className="field">
			      <label htmlFor="description">Description</label>
			      <input 
			      	onChange={(e) => setDescription(e.target.value)}
			      	id="description"
			      	type="text" 
			      	placeholder="Description"/>
			    </div>
			    <div className="field">
			      <label htmlFor="amount">Amount</label>
			      <input 
			      	onChange={(e) => setAmount(e.target.value)}
			      	id="amount"
			      	type="text" 
			      	placeholder="$$"/>
			    </div>
			    <div className="field">
			     <label>Type</label>
						<select onChange={(e) => setType(e.target.value)} id="budget-type" className="ui search dropdown" defaultValue={'DEFAULT'} style={{padding: '0 0 0 5px'}}>
						  <option value="DEFAULT" disabled>Select Type</option>
						  <option value="1">Income</option>
						  <option value="0">Expense</option>
						</select>
			    </div>
			    <button 
			    	onClick={onBudgetSubmit}
			    	className="ui button" 
			    	type="submit">
			    	Submit
			    </button>
			  </div>
			</form>
		</div>
	);
}

export default BudgetInput;
