import React, { useState } from 'react';

const BudgetInput = () => {

	const [budgetList, setBudgetList] = [{
		description: '',
		budget: 0
	}];

	const onBudgetSubmit = (event) => {
		event.preventDefault();
		let description = document.querySelector('#description').value;
		let amount = document.querySelector('#amount').value;
		setBudgetList((budgetList) => [...budgetList, {description: description, amount: amount}])
		console.log(budgetList);
		amount='';
		description='';
	}

	return (
		<div className="ui container">
			<form className="ui form">
			  <div className="fields">
			    <div className="field">
			      <label htmlFor="description">Description</label>
			      <input 
			      	id="description"
			      	type="text" 
			      	placeholder="Description"/>
			    </div>
			    <div className="field">
			      <label htmlFor="amount">Amount</label>
			      <input 
			      	id="amount"
			      	type="text" 
			      	placeholder="$$"/>
			    </div>
			    <div className="field">
			     <label>Type</label>
						<select id="budget-type" className="ui selection dropdown" style={{padding: '0 0 0 5px'}}>
						  <option value="1">Income</option>
						  <option value="0">Expense</option>
						</select>
			    </div>
			    <button 
			    	onClick={onBudgetSubmit}
			    	className="ui button" 
			    	type="submit">
			    	Submit
			    </button>
			  </div>
			</form>
		</div>
	);
}

export default BudgetInput;
