import React from 'react';

const BudgetCard = ({amount, description, type}) => {

	return (
	<div>
		<div className="ui small horizontal statistic">
		  <div className="value" style={{color: type === 1 ? 'green' : 'red'}}>
		    {amount ? type === 1 ? `$${amount}` : `-$${amount}`: ''}
		  </div>
		  <div className="label" style={{color: type === 1 ? 'green' : 'red'}}>
		    {description}
		  </div>
		</div>
	</div>
	);
}


export default BudgetCard;
