import React from 'react';

const Balance = ({data}) => {

	const calculateBalance = () => {
		return data.reduce((acc, num) => {
			return acc + num.amount }, 0)
	}

	return (
		<div className="ui container" id="balance-container">
			<h2>{data[0].amount ? 'Balance' : ''}</h2>
			<h5 id="balance-text" style={{color: calculateBalance() > 0 ? 'green' : 'red'}}>
				{data[0].amount ? `$${calculateBalance()}` : ''}</h5>
		</div>
	);
}



export default Balance;

