import React, { useState } from 'react';
import BudgetInput from './BudgetInput';
import BudgetList from './BudgetList';
import Balance from './Balance';
import Reset from './Reset';
import './App.css';

const App = () => {

	const [data, setData] = useState([{ amount: null, description: '', type: null}]);

	const fetchTransactions = async (amount, description, type) => {
		// setData(test);
		let response = await fetch('http://localhost:3001/submit', {
			method: 'put',
			headers: { 'Content-type': 'application/json'},
			body: JSON.stringify({
				amount: amount,
				description: description,
				type: type
			})
		})
		response = await response.json();
		response = response.map(transaction => {
			transaction.amount = parseInt(transaction.amount);
			return transaction;
		})
		console.log(response);
		setData(response);
	}

	const onReset = async () => {
		await fetch('http://localhost:3001/reset', {
			method: 'put',
			headers: { 'Content-type': 'application/json'},
		})
		setData([{ amount: null, description: '', type: null}]);
	}


	return (
		<div>
			<div id="budget-input" className="ui container">
				<BudgetInput onSubmit={fetchTransactions} />
			</div>
			<div id="reset-button" className="ui container">
				<Reset onReset={onReset} />
			</div>
			<BudgetList data={data}/> 
			<Balance data={data}/> 
		</div>

	);
}

export default App;
