import React, { useState } from 'react';

const BudgetInput = () => {

	const [amount, setAmount] = useState(0);
	const [description, setDescription] = useState('');
	const [type, setType] = useState(true);


	const onBudgetSubmit = event => {
		event.preventDefault();
		setDescription(document.querySelector('#description').value);
		setAmount(document.querySelector('#amount').value);
		const dropdown = document.querySelector('#budget-type').value;
		if (dropdown === 0) {
			setType(false)
		} else {
			setType(true)
		}
		document.querySelector('#description').value = '';
		document.querySelector('#amount').value = '';
		console.log(amount, description, type)
	}

	return (
		<div className="ui container">
			<form className="ui form">
			  <div className="fields">
			    <div className="field">
			      <label htmlFor="description">Description</label>
			      <input 
			      	id="description"
			      	type="text" 
			      	placeholder="Description"/>
			    </div>
			    <div className="field">
			      <label htmlFor="amount">Amount</label>
			      <input 
			      	id="amount"
			      	type="text" 
			      	placeholder="$$"/>
			    </div>
			    <div className="field">
			     <label>Type</label>
						<select id="budget-type" className="ui selection dropdown" style={{padding: '0 0 0 5px'}}>
						  <option value="1">Income</option>
						  <option value="0">Expense</option>
						</select>
			    </div>
			    <button 
			    	onClick={onBudgetSubmit}
			    	className="ui button" 
			    	type="submit">
			    	Submit
			    </button>
			  </div>
			</form>
		</div>
	);
}

export default BudgetInput;
